tinymce.PluginManager.add('kleurimage', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('kleurimage', {
        icon: 'image',
        onclick: function() {
            // Open window
            var baseUrl = editor.getParam("baseUrl");
            // console.log(url);
            editor.windowManager.open({
                title: 'Media',
                url: baseUrl + '/form',
                width : 1024,
                height : 600
            });
        }
    });
});
